import React, { useState } from 'react';
import axios from 'axios';
import FileUploader from './components/FileUploader';
import ProfileViewer from './components/ProfileView';
import './App.css';

function App() {
  console.log("check");
  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFileUpload = async (file) => {
    try {
      setLoading(true);
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post(
        'http://localhost:8000/api/profile/',
        formData,
        {
          // Axios config
          validateStatus: (status) => status < 500 // Treat 5xx as errors
        }
      );

      if (response.data.detail) { // Handle FastAPI error responses
        throw new Error(response.data.detail);
      }

      setProfileData(response.data);
      setError('');
    } catch (err) {
      console.error('Full error:', err);
      setError(err.response?.data?.detail || err.message || 'Unknown error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header>
        <h1>CSV Data Profiler</h1>
        <FileUploader onFileSelect={handleFileUpload} />
        {loading && <p>Analyzing your file...</p>}
        {error && <p className="error">{error}</p>}
      </header>

      {profileData && <ProfileViewer profileData={profileData} />}
    </div>
  );
}

export default App;
