import React, { useState } from 'react';
import axios from 'axios';
import FileUploader from './components/FileUploader';
import ProfileViewer from './components/ProfileView';
import './App.css';

function App() {
  console.log("check");
  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFileUpload = async (file) => {
    try {
      setLoading(true);
      const formData = new FormData();
      formData.append('file', file);  // Key must be 'file' to match FastAPI's File(...)

      const response = await axios.post(
        'http://localhost:8000/api/profile/',  // Use localhost instead of 127.0.0.1
        formData
        // Remove explicit headers - Axios auto-sets multipart/form-data with boundary
      );

      setProfileData(response.data);
      setError('');
    } catch (err) {
      console.error('API Error:', err.response?.data || err.message);
      setError(err.response?.data?.detail || 'Error processing file');
    } finally {
      setLoading(false);
    }
    try {
      const response = await fetch('http://localhost:8000/api/profile/', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) throw new Error('HTTP error');
      const data = await response.json();
      setProfileData(data);
    } catch (err) {
      console.error('Fetch error:', err);
    }
  };

  return (
    <div className="App">
      <header>
        <h1>CSV Data Profiler</h1>
        <FileUploader onFileSelect={handleFileUpload} />
        {loading && <p>Analyzing your file...</p>}
        {error && <p className="error">{error}</p>}
      </header>

      {profileData && <ProfileViewer profileData={profileData} />}
    </div>
  );
}

export default App;
